import React from "react";
import { BadgeCheck, Truck, Shield } from "lucide-react";

export function WhyChooseUs() {
  return (
    <section className="py-20 bg-navy-950 text-white">
      <div className="max-w-7xl mx-auto grid md:grid-cols-3 gap-8 text-center">
        <div>
          <BadgeCheck className="mx-auto mb-3 text-electric-400" />
          <h3>100% Genuine Products</h3>
        </div>
        <div>
          <Truck className="mx-auto mb-3 text-electric-400" />
          <h3>Fast Delivery</h3>
        </div>
        <div>
          <Shield className="mx-auto mb-3 text-electric-400" />
          <h3>Warranty Available</h3>
        </div>
      </div>
    </section>
  );
}
