import React from "react";
import { motion } from "framer-motion";
import {
  ShieldCheck,
  Truck,
  Lock,
  ArrowRight,
  Zap,
} from "lucide-react";
import heroImage from "../assets/hero.jpg";


export function HeroSection() {
  const sectionStyle = {
    position: "relative",
    backgroundColor: "#061523",
    color: "white",
    overflow: "hidden",
    padding: "120px 16px",
  };

  const containerStyle = {
    maxWidth: "1200px",
    margin: "0 auto",
    position: "relative",
    zIndex: 2,
  };

  const backgroundImageStyle = {
  position: "absolute",
  inset: 0,
  backgroundImage: `url(${heroImage})`,
  backgroundSize: "cover",
  backgroundPosition: "center",
  opacity: 0.08,
};


  const gradientOverlay = {
    position: "absolute",
    inset: 0,
    background:
      "linear-gradient(to right, #061523, #102a43, #061523)",
    opacity: 0.9,
  };

  const badgeStyle = {
    display: "inline-flex",
    alignItems: "center",
    padding: "6px 14px",
    borderRadius: "999px",
    backgroundColor: "#102a43",
    border: "1px solid #1f3a5f",
    color: "#ffec1f",
    fontSize: "14px",
    marginBottom: "30px",
  };

  const headingStyle = {
    fontSize: "56px",
    fontWeight: "bold",
    lineHeight: "1.2",
    marginBottom: "24px",
  };

  const highlightStyle = {
    background: "linear-gradient(to right, #ffec1f, #fff54d)",
    WebkitBackgroundClip: "text",
    color: "transparent",
  };

  const paragraphStyle = {
    fontSize: "20px",
    color: "#cbd5e1",
    maxWidth: "700px",
    marginBottom: "40px",
  };

  const primaryButton = {
    padding: "16px 32px",
    backgroundColor: "#ffec1f",
    color: "#061523",
    fontWeight: "bold",
    borderRadius: "10px",
    border: "none",
    cursor: "pointer",
    display: "flex",
    alignItems: "center",
    marginRight: "16px",
    boxShadow: "0 0 20px rgba(255,236,31,0.4)",
  };

  const secondaryButton = {
    padding: "16px 32px",
    backgroundColor: "transparent",
    border: "2px solid white",
    color: "white",
    fontWeight: "bold",
    borderRadius: "10px",
    cursor: "pointer",
  };

  const buttonContainer = {
    display: "flex",
    flexWrap: "wrap",
    gap: "16px",
    marginBottom: "60px",
  };

  const trustGrid = {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
    gap: "30px",
    borderTop: "1px solid #1f3a5f",
    paddingTop: "30px",
  };

  const trustItem = {
    display: "flex",
    alignItems: "center",
    gap: "12px",
  };

  const glowStyle = {
    position: "absolute",
    top: "50%",
    right: "-150px",
    transform: "translateY(-50%)",
    width: "400px",
    height: "400px",
    backgroundColor: "#ffec1f",
    opacity: 0.05,
    filter: "blur(120px)",
    borderRadius: "50%",
    zIndex: 0,
  };

  return (
    <section style={sectionStyle}>
      {/* Background */}
      <div style={backgroundImageStyle}></div>
      <div style={gradientOverlay}></div>
      <div style={glowStyle}></div>

      <div style={containerStyle}>
        <motion.div initial={{ opacity: 0, y: 30 }} animate={{ opacity: 1, y: 0 }}>
          
          {/* Badge */}
          <div style={badgeStyle}>
            <Zap size={16} style={{ marginRight: "8px" }} />
            Sri Lanka's #1 Electronics Store
          </div>

          {/* Heading */}
          <h1 style={headingStyle}>
            Powering Sri Lanka with <br />
            <span style={highlightStyle}>Quality Electronics</span>
          </h1>

          {/* Paragraph */}
          <p style={paragraphStyle}>
            Your trusted destination for electrical and electronic products
            at affordable prices. From industrial equipment to smart home
            solutions, we have it all.
          </p>

          {/* Buttons */}
          <div style={buttonContainer}>
            <button style={primaryButton}>
              Shop Now
              <ArrowRight size={18} style={{ marginLeft: "8px" }} />
            </button>

            <button style={secondaryButton}>
              View Products
            </button>
          </div>

          {/* Trust Badges */}
          <div style={trustGrid}>
            <div style={trustItem}>
              <ShieldCheck size={28} color="#ffec1f" />
              <div>
                <strong>Quality Guaranteed</strong>
                <p style={{ fontSize: "14px", color: "#94a3b8" }}>
                  100% Genuine Products
                </p>
              </div>
            </div>

            <div style={trustItem}>
              <Truck size={28} color="#ffec1f" />
              <div>
                <strong>Fast Delivery</strong>
                <p style={{ fontSize: "14px", color: "#94a3b8" }}>
                  Island-wide Shipping
                </p>
              </div>
            </div>

            <div style={trustItem}>
              <Lock size={28} color="#ffec1f" />
              <div>
                <strong>Secure Payments</strong>
                <p style={{ fontSize: "14px", color: "#94a3b8" }}>
                  Cash on Delivery & Cards
                </p>
              </div>
            </div>
          </div>

        </motion.div>
      </div>
    </section>
  );
}
