import React from "react";
import { motion } from "framer-motion";
import { ArrowRight, Zap } from "lucide-react";

export function HeroSection() {
  const sectionStyle = {
    backgroundColor: "#061523",
    color: "white",
    padding: "96px 16px",
    textAlign: "center",
  };

  const badgeStyle = {
    display: "inline-flex",
    alignItems: "center",
    padding: "4px 12px",
    backgroundColor: "#102a43",
    borderRadius: "999px",
    color: "#ffec1f",
    marginBottom: "24px",
  };

  const headingStyle = {
    fontSize: "3rem",
    fontWeight: "bold",
    marginBottom: "24px",
  };

  const highlightStyle = {
    color: "#ffec1f",
  };

  const paragraphStyle = {
    fontSize: "1.2rem",
    color: "#ccc",
    maxWidth: "700px",
    margin: "0 auto 32px",
  };

  const buttonStyle = {
    backgroundColor: "#ffec1f",
    color: "#061523",
    padding: "12px 24px",
    borderRadius: "12px",
    fontWeight: "bold",
    border: "none",
    cursor: "pointer",
    display: "inline-flex",
    alignItems: "center",
  };

  return (
    <section style={sectionStyle}>
      <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
        <div style={badgeStyle}>
          <Zap style={{ width: "16px", height: "16px", marginRight: "8px" }} />
          Sri Lanka's #1 Electronics Store
        </div>

        <h1 style={headingStyle}>
          Powering Sri Lanka with{" "}
          <span style={highlightStyle}>Quality Electronics</span>
        </h1>

        <p style={paragraphStyle}>
          Your trusted destination for electrical and electronic products.
        </p>

        <button style={buttonStyle}>
          Shop Now <ArrowRight style={{ marginLeft: "8px" }} />
        </button>
      </motion.div>
    </section>
  );
}
