import React from "react";
import { motion } from "framer-motion";
import { ArrowRight, Zap } from "lucide-react";

export function HeroSection() {
  return (
    <div className="bg-navy-950 text-white py-24">
      <div className="max-w-7xl mx-auto px-4">
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
          <div className="inline-flex items-center px-3 py-1 bg-navy-800 rounded-full text-electric-400 mb-6">
            <Zap className="w-4 h-4 mr-2" fill="currentColor" />
            Sri Lanka's #1 Electronics Store
          </div>

          <h1 className="text-5xl font-bold mb-6">
            Powering Sri Lanka with{" "}
            <span className="text-electric-400">Quality Electronics</span>
          </h1>

          <p className="text-gray-300 mb-8">
            Your trusted destination for electrical and electronic products.
          </p>

          <button className="bg-electric-400 text-navy-950 px-6 py-3 rounded-lg font-bold flex items-center">
            Shop Now <ArrowRight className="ml-2" />
          </button>
        </motion.div>
      </div>
    </div>
  );
}
