import React from "react";

const products = [
  { name: "LED Bulb 12W", price: 450 },
  { name: "Circuit Breaker", price: 1250 },
  { name: "Extension Cord 5m", price: 850 },
];

export function FeaturedProducts() {
  return (
    <section className="py-20 bg-white">
      <div className="max-w-7xl mx-auto grid md:grid-cols-3 gap-8">
        {products.map((product, index) => (
          <div key={index} className="border p-6 rounded-xl shadow-md">
            <h3 className="font-bold text-lg">{product.name}</h3>
            <p className="text-gray-600">LKR {product.price}</p>
            <button className="mt-4 bg-navy-900 text-white px-4 py-2 rounded">
              Buy Now
            </button>
          </div>
        ))}
      </div>
    </section>
  );
}
