import React from "react";
import { Cable, Lightbulb, Tv, Factory, Wrench } from "lucide-react";

const categories = [
  { name: "Electrical Supplies", icon: Cable },
  { name: "LED & Lighting", icon: Lightbulb },
  { name: "Home Electronics", icon: Tv },
  { name: "Industrial Equipment", icon: Factory },
  { name: "Tools & Accessories", icon: Wrench },
];

export function CategorySection() {
  const sectionStyle = {
    backgroundColor: "#102a43",
    color: "white",
    padding: "80px 16px",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(180px, 1fr))",
    gap: "20px",
  };

  const cardStyle = {
    backgroundColor: "#243b53",
    padding: "24px",
    borderRadius: "16px",
    textAlign: "center",
    cursor: "pointer",
    transition: "all 0.3s ease",
    border: "2px solid transparent",
  };

  const cardHoverStyle = {
    border: "2px solid #ffec1f",
  };

  return (
    <section style={sectionStyle}>
      {categories.map((cat, index) => {
        const Icon = cat.icon;
        return (
          <div
            key={index}
            style={cardStyle}
            onMouseEnter={(e) => (e.currentTarget.style.border = "2px solid #ffec1f")}
            onMouseLeave={(e) => (e.currentTarget.style.border = "2px solid transparent")}
          >
            <Icon style={{ margin: "0 auto 12px", width: "32px", height: "32px", color: "#ffec1f" }} />
            <h3>{cat.name}</h3>
          </div>
        );
      })}
    </section>
  );
}
