import React from "react";

const products = [
  { name: "LED Bulb 12W", price: 450 },
  { name: "Circuit Breaker", price: 1250 },
  { name: "Extension Cord 5m", price: 850 },
];

export function FeaturedProducts() {
  const sectionStyle = {
    backgroundColor: "white",
    padding: "80px 16px",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
    gap: "24px",
  };

  const cardStyle = {
    backgroundColor: "white",
    border: "1px solid #ddd",
    borderRadius: "16px",
    overflow: "hidden",
    display: "flex",
    flexDirection: "column",
    padding: "16px",
  };

  const buttonStyle = {
    marginTop: "12px",
    backgroundColor: "#061523",
    color: "#ffec1f",
    border: "none",
    padding: "10px",
    borderRadius: "10px",
    cursor: "pointer",
  };

  return (
    <section style={sectionStyle}>
      {products.map((product, index) => (
        <div key={index} style={cardStyle}>
          <h3 style={{ fontWeight: "bold", fontSize: "1.1rem", marginBottom: "8px" }}>
            {product.name}
          </h3>
          <p>LKR {product.price}</p>
          <button style={buttonStyle}>Buy Now</button>
        </div>
      ))}
    </section>
  );
}
