import React, { useState } from "react";
import { Zap, Search, ShoppingCart, User, Menu, X, Heart } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

export function Navbar() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <nav className="sticky top-0 z-50 bg-navy-950 border-b border-navy-800 text-white shadow-lg">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-20">

          {/* Logo */}
          <div className="flex items-center cursor-pointer">
            <div className="bg-electric-400 p-1.5 rounded-full mr-2">
              <Zap className="h-6 w-6 text-navy-950" fill="currentColor" />
            </div>
            <span className="font-bold text-xl">
              NetTec <span className="text-electric-400">Electronic</span>
            </span>
          </div>

          {/* Desktop Search */}
          <div className="hidden md:flex flex-1 max-w-md mx-8">
            <div className="relative w-full">
              <Search className="absolute left-3 top-2.5 h-5 w-5 text-navy-400" />
              <input
                type="text"
                placeholder="Search products..."
                className="w-full pl-10 pr-3 py-2 bg-navy-900 border border-navy-700 rounded-full text-gray-300 focus:border-electric-400 focus:outline-none"
              />
            </div>
          </div>

          {/* Desktop Icons */}
          <div className="hidden md:flex items-center space-x-6">
            <Heart className="h-6 w-6 cursor-pointer hover:text-electric-400" />
            <ShoppingCart className="h-6 w-6 cursor-pointer hover:text-electric-400" />
            <User className="h-6 w-6 cursor-pointer hover:text-electric-400" />
          </div>

          {/* Mobile Menu Button */}
          <div className="md:hidden">
            <button onClick={() => setIsMenuOpen(!isMenuOpen)}>
              {isMenuOpen ? <X /> : <Menu />}
            </button>
          </div>
        </div>
      </div>

      <AnimatePresence>
        {isMenuOpen && (
          <motion.div
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: "auto" }}
            exit={{ opacity: 0, height: 0 }}
            className="md:hidden bg-navy-900 p-4"
          >
            <div className="space-y-3 text-white">
              <p>Home</p>
              <p>Categories</p>
              <p>Products</p>
              <p>Contact</p>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </nav>
  );
}
