import React from "react";
import { Cable, Lightbulb, Tv, Factory, Wrench } from "lucide-react";

const categories = [
  { name: "Electrical Supplies", icon: Cable },
  { name: "LED & Lighting", icon: Lightbulb },
  { name: "Home Electronics", icon: Tv },
  { name: "Industrial Equipment", icon: Factory },
  { name: "Tools & Accessories", icon: Wrench },
];

export function CategorySection() {
  return (
    <section className="py-20 bg-navy-900 text-white">
      <div className="max-w-7xl mx-auto grid grid-cols-2 md:grid-cols-5 gap-6">
        {categories.map((cat, index) => (
          <div key={index} className="bg-navy-800 p-6 rounded-xl text-center hover:border-electric-400 border border-navy-700">
            <cat.icon className="mx-auto h-8 w-8 text-electric-400 mb-3" />
            <h3>{cat.name}</h3>
          </div>
        ))}
      </div>
    </section>
  );
}
