import React from "react";
import { BadgeCheck, Truck, Shield } from "lucide-react";

export function WhyChooseUs() {
  const sectionStyle = {
    backgroundColor: "#061523",
    color: "white",
    padding: "80px 16px",
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
    gap: "24px",
    textAlign: "center",
  };

  const cardStyle = {
    backgroundColor: "#243b53",
    padding: "24px",
    borderRadius: "16px",
  };

  const iconStyle = {
    marginBottom: "12px",
    color: "#ffec1f",
  };

  const features = [
    { icon: BadgeCheck, title: "100% Genuine Products" },
    { icon: Truck, title: "Fast Delivery" },
    { icon: Shield, title: "Warranty Available" },
  ];

  return (
    <section style={sectionStyle}>
      {features.map((f, index) => {
        const Icon = f.icon;
        return (
          <div key={index} style={cardStyle}>
            <Icon style={iconStyle} />
            <h3>{f.title}</h3>
          </div>
        );
      })}
    </section>
  );
}
